/*\
title: $:/core/modules/upgraders/system.js
type: application/javascript
module-type: upgrader

Upgrader module that suppresses certain system tiddlers that shouldn't be imported

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var DONT_IMPORT_LIST = ["$:/Import"],
	DISABLE_PREFIX_LIST = ["$:/temp/","$:/state/","$:/StoryList","$:/HistoryList"],
	WARN_IMPORT_PREFIX_LIST = ["$:/core/modules/"];

exports.upgrade = function(wiki,titles,tiddlers) {
	var self = this,
		messages = {},
		showAlert = false;
	// Check for tiddlers on our list
	$tw.utils.each(titles,function(title) {
		if(DONT_IMPORT_LIST.indexOf(title) !== -1) {
			tiddlers[title] = Object.create(null);
			messages[title] = $tw.language.getString("Import/Upgrader/System/Suppressed");
		} else {
			for(var t=0; t<DISABLE_PREFIX_LIST.length; t++) {
				var prefix = DISABLE_PREFIX_LIST[t];
				if(title.substr(0,prefix.length) === prefix) {
					messages[title] = $tw.language.getString("Import/Upgrader/Disabled/Tiddler");
				}
			}
			for(var t=0; t<WARN_IMPORT_PREFIX_LIST.length; t++) {
				var prefix = WARN_IMPORT_PREFIX_LIST[t];
				if(title.substr(0,prefix.length) === prefix && wiki.isShadowTiddler(title)) {
					showAlert = true;
					messages[title] = $tw.language.getString("Import/Upgrader/System/Warning");
				}
			}
		}
	});
	if(showAlert) {
		var logger = new $tw.utils.Logger("import");
		logger.alert($tw.language.getString("Import/Upgrader/System/Alert"));
	}
	return messages;
};

})();

$:/plugins/wikilabs/bundler/th-before-im.text:
/*\
title: $:/plugins/wikilabs/bundler/th-before-importing.js
type: application/javascript
module-type: startup

A startup module to log imported tiddlers to the import.bundle tiddler.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false, exports: true */
"use strict";

// Export name and synchronous status
exports.name = "bundlerbeforeimport";
exports.platforms = ["browser"];
exports.after = ["startup"];
exports.synchronous = true;

// Favicon tiddler
var ENABLE_IMPORT_HEADER = "$:/config/wikilabs/bundler/enableImportHeader",
	IMPORT_HEADER_TEMPLATE = "$:/config/wikilabs/bundler/importHeaderTemplate",
	// Next line needs to be that name because of compatibility reasons

	ENABLE_IMPORT_BUNDLE = "$:/config/wikilabs/enableImportBundle",
	IMPORT_LOG_TITLE = "import.bundle";

exports.startup = function() {
	$tw.hooks.addHook("th-before-importing",function(importTiddler) {
		var logTiddler,
			logHeader = $tw.wiki.renderTiddler("text/plain",IMPORT_HEADER_TEMPLATE) || "New import started!";

		if($tw.wiki.checkTiddlerText(ENABLE_IMPORT_HEADER,"yes")) {
			if($tw.wiki.checkTiddlerText(ENABLE_IMPORT_BUNDLE,"yes")) {
				logTiddler = $tw.wiki.getTiddler(IMPORT_LOG_TITLE) || new $tw.Tiddler($tw.wiki.getCreationFields(), $tw.wiki.getModificationFields(), {title: IMPORT_LOG_TITLE, text: "", type: "text/plain", tags: ["$:/tags/Bundle"]});

				var lineFeed = (logTiddler.fields.text) ? "\n" : "";

				$tw.wiki.addTiddler( new $tw.Tiddler( logTiddler, 
					{text: logTiddler.fields.text + lineFeed + "[[--- " + logHeader + " ---]]\n"}));
			}
		}
		return importTiddler;
	});
};

})();

$:/plugins/wikilabs/bundler/th-importing.text:
/*\
title: $:/plugins/wikilabs/bundler/th-importing-tiddler.js
type: application/javascript
module-type: startup

A startup module to log imported tiddlers to the import.bundle tiddler.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false, exports: true */
"use strict";

// Export name and synchronous status
exports.name = "bundlerimporttiddler";
exports.platforms = ["browser"];
exports.after = ["startup"];
exports.synchronous = true;

// Favicon tiddler
var ENABLE_LOG_TITLE = "$:/config/wikilabs/enableImportBundle",
	IMPORT_LOG_TITLE = "import.bundle";

exports.startup = function() {
	$tw.hooks.addHook("th-importing-tiddler",function(tiddler) {
		var logTiddler,
			tidTitle = tiddler.fields.title;

		if($tw.wiki.checkTiddlerText(ENABLE_LOG_TITLE,"yes")) {
			logTiddler = $tw.wiki.getTiddler(IMPORT_LOG_TITLE) || new $tw.Tiddler($tw.wiki.getCreationFields(), $tw.wiki.getModificationFields(), {title: IMPORT_LOG_TITLE, text: "", type: "text/plain", tags: ["$:/tags/Bundle"]});

			if (tidTitle.indexOf(" ") !== -1) {
				$tw.wiki.addTiddler( new $tw.Tiddler( logTiddler, {text: logTiddler.fields.text + "[[" + tidTitle + "]]\n"}));
			} else {
				$tw.wiki.addTiddler( new $tw.Tiddler( logTiddler, {text: logTiddler.fields.text + tidTitle + "\n"}));
			}
		}
		return tiddler;
	});
};

})();

$:/plugins/wikilabs/bundler/upgrade.js.text:
/*\
title: $:/plugins/wikilabs/bundler/upgrade.js
type: application/javascript
module-type: upgrader

This module checks, if tiddlers, that are imported already exist. If they do, they are disabled and a tiddler with a new name will be created. ge: "New Tiddler (1)". This option is configurable.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var ENABLE_OVERWRITE_CHECK = "$:/config/wikilabs/enableOverwriteCheck";

exports.upgrade = function(wiki,titles,tiddlers) {
	var self = this,
		messages = {},
		overwriteCheck = $tw.wiki.checkTiddlerText(ENABLE_OVERWRITE_CHECK,"yes");

	$tw.utils.each(titles,function(title) {
		var tiddler = {};
		// If the tiddler has been removed, there will be no fields.
		if (wiki.tiddlerExists(title) && tiddlers[title] && (tiddlers[title].title === title) ) {
			messages[title] = "Existing tiddler will be overwritten!";
			// create new tiddler with a new name
			if (overwriteCheck && !wiki.isSystemTiddler(title)) {
				messages[title] = "Tiddler exists - A new name will be used!";
				tiddler = new $tw.Tiddler(tiddlers[title], {"title": wiki.generateNewTitle(title) });
				tiddlers[tiddler.fields.title] = tiddler.fields;
				// remove the original tiddler
				tiddlers[title] = Object.create(null);
			}
		}
	});
	return messages;
	}

})();
